import React, { Component } from "react";

const anime_art = [
  {i: "https://66.media.tumblr.com/d46e8b697a60f10697e74d6d39b9f87b/8ce71a3daca603f9-df/s500x750/0ed9ccaff8d346a3b7b057614ab5088785c3d918.gifv", c: "https://cherrythunder.tumblr.com/post/186416423768/c-h-e-e-r-s"}
];


export default class LoFi extends Component {
  constructor() {
    super();
    this.state = {
      art_index: Math.floor(Math.random() * anime_art.length),
    };
  }

  componentDidMount() {
    setInterval(() => {
      this.setState({
        art_index: (this.state.art_index + 1) % anime_art.length,
      });
    }, 5000);
  }

  render() {
    return (
      <div
        className="wrapper"
        style={{
          background: `url(${
            anime_art[this.state.art_index].i
          }) center center / cover no-repeat fixed`,
          backgroundSize: "cover",
          backgroundRepeat: "no-repeat",
          backgroundPosition: "center center",

          width: "100%",
          height: "100%",
          position: "fixed",
          top: 0,
          left: 0,
          zIndex: -100,
        }}
      >
        <iframe
          className="player"
          width="20%"
          height="450"
          scrolling="no"
          frameborder="no"
          src="https://w.soundcloud.com/player/?url=https%3A//api.soundcloud.com/playlists/601710774&color=%239c5cba&hide_related=false&show_comments=true&show_user=true&show_reposts=false&show_teaser=true"
        ></iframe>
      </div>
    );
  }
}
