const anime_art = [
  "https://66.media.tumblr.com/35cd8d3a5a350997ef3f7a71350efa9e/1ed772b41ffa92e6-2b/s500x750/48bae543cc99d3b468d7b52ec73687c7a4c32df4.gifv",
